open Belenios_platform
open Platform
open Belenios_core
open Serializable_j
open Signatures

let get_ff_params = function
  | "BELENIOS-2048" ->
      {
        g =
          Z.of_string
            "";
        p =
          Z.of_string
            "";
        q =
          Z.of_string
            "78571733251071885079927659812671450121821421258408794611510081919805623223441";
        embedding = None;
      }
  | "RFC-3526-2048" ->
      {
        g = Z.of_string "2";
        p =
          Z.of_string
            "";
        q =
          Z.of_string
            "";
        embedding = Some { padding = 8; bits_per_int = 8 };
      }
  | _ -> raise Not_found

let ed25519 () : (module GROUP) =
  match Libsodium_stubs.make () with
  | None -> (module Ed25519_pure)
  | Some stubs ->
      let module S = (val stubs) in
      let module G = Ed25519_libsodium.Make (S) in
      (module G)

let of_string x =
  match get_ff_params x with
  | params ->
      let module G = (val Group_field.make x params : Group_field.GROUP) in
      (module G : GROUP)
  | exception Not_found -> (
      match x with
      | "Ed25519" -> ed25519 ()
      | _ -> Printf.ksprintf failwith "unknown group: %s" x)
